import { React, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { addSkill } from '../services/skills';
import { getOnePlayer } from '../services/players';

export default function PlayerDetail(props) {
  const [player, setPlayer] = useState(null);
  const [skillId, setSkillId] = useState('')
  const { id } = useParams();

  useEffect(() => {
    const fetchPlayer = async () => {
      const playerData = await getOnePlayer(id);
      setPlayer(playerData);
    }
    fetchPlayer();
  }, [id])

  const handleSubmit = async (e) => {
    e.preventDefault();
    const player = await addSkill(skillId, id);
    setPlayer(player);
  }

  const handleChange = (e) => {
    const { value } = e.target;
    setSkillId(value);
  }

  return (
    <div>
      <div>
        <h2>{player?.name}</h2>
        <img src={player?.img} alt="player picture"></img>
        <h3>{player?.position}</h3>
        {player?.skills.map(skill => (
          <p key={skill.id}>{skill.name}</p>
        ))}
        <form onSubmit={handleSubmit}>
          <select defaultValue='default' onChange={handleChange}>
            <option disabled value='default'>-- Select a skill --</option>
            {props.skills.map(skill => (
              <option value={skill.id} key={skill.id}>{skill.name}</option>
            ))}
          </select>
          <button>add</button>
        </form>
      </div>
      <div>
        <React.Fragment key={player.id}>
          {
            player.user_id === props.currentUser?.id &&
            <>
              <Link to={`/players/${player.id}/edit`}><button>Edit</button></Link>
              <button onClick={() => props.handleDelete(player.id)}>Delete</button>
            </>
          }
        </React.Fragment>
      </div>
    </div>
  )
}
